%jdbc(hive_interactive)

drop database if exists movies cascade;

create database movies;
use movies;

-- Contains the following information for titles
CREATE EXTERNAL TABLE movie_title (
                        tconst string,
                        titleType string,
                        primaryTitle string,
                        originalTitle string,
                        isAdult string,
                        startYear string,
                        endYear string,
                        runtimeMinutes string,
                        genres ARRAY<STRING>
                        ) ROW FORMAT DELIMITED
FIELDS TERMINATED BY '\t' LOCATION '/user/zeppelin/movie_title/';

-- Load data
LOAD DATA  INPATH '/labs/data/title.basics.tsv' OVERWRITE INTO TABLE movie_title;

-- Remove header for movie_title
ALTER TABLE movie_title SET TBLPROPERTIES ("skip.header.line.count"="1");


-- Contains the information for directors and writers
CREATE EXTERNAL TABLE movie_crew(
                        tconst string,
                        directors ARRAY<STRING>,
                        writers ARRAY<STRING>
                        ) ROW FORMAT DELIMITED
FIELDS TERMINATED BY '\t' LOCATION '/user/zeppelin/movie_crew/';

-- Load data movie_crew
LOAD DATA  INPATH '/labs/data/title.crew.tsv' OVERWRITE INTO TABLE movie_crew;

-- Remove header for movie_crew
ALTER TABLE movie_crew SET TBLPROPERTIES ("skip.header.line.count"="1");

-- Contains the information for names
CREATE EXTERNAL TABLE movie_crew_info(
                        nconst string,
                        primaryName string,
                        birthYear string,
                        deathYear string,
                        primaryProfession ARRAY<STRING>,
                        knownForTitles ARRAY<STRING>
                        ) ROW FORMAT DELIMITED
FIELDS TERMINATED BY '\t' LOCATION '/user/zeppelin/movie_crew_info/';

-- Load data movie_crew_info
LOAD DATA  INPATH '/labs/data/name.basics.tsv' OVERWRITE INTO TABLE movie_crew_info;

-- Remove header for movie_crew_info
ALTER TABLE movie_crew_info SET TBLPROPERTIES ("skip.header.line.count"="1");


-- Ratings and vote information for titles
CREATE EXTERNAL TABLE ratings(
                        tconst string,
                        averageRting double,
                        numVotes int
                        ) ROW FORMAT DELIMITED
FIELDS TERMINATED BY '\t' LOCATION '/user/zeppelin/ratings/';


-- Load data ratings
LOAD DATA  INPATH '/labs/data/title.ratings.tsv' OVERWRITE INTO TABLE ratings;

-- Remove header for ratings
ALTER TABLE ratings SET TBLPROPERTIES ("skip.header.line.count"="1");

%md
We are going to use the new movie database. You can do a describe on the tables.

~~~
use movies;
describe movie_title;
describe movie_crew;
describe movie_crew_info;
describe ratings;
~~~

Review the tables to determine keys to join.

%jdbc(hive_interactive)
Select movie_title.titleType,movie_title.originalTitle,movie_title.startYear, movie_Crew_info.primaryname,movie_crew_info.birthyear,movie_crew_info.primaryprofession from movie_title JOIN movie_crew_info 
ON (SUBSTR(movie_title.tconst,3,9) = SUBSTR(movie_crew_info.nconst,3,9)) limit 5;

%jdbc(hive_interactive)
Explain Select movie_title.titleType, movie_title.primaryTitle, movie_title.originalTitle, ratings.averageRting from movie_title JOIN ratings ON (movie_title.tconst=ratings.tconst) limit 5;
Explain Select movie_title.titleType,movie_title.originalTitle,movie_title.startYear, movie_Crew_info.primaryname,movie_crew_info.birthyear,movie_crew_info.primaryprofession from movie_title JOIN movie_crew_info 
ON (SUBSTR(movie_title.tconst,3,9) = SUBSTR(movie_crew_info.nconst,3,9)) limit 5;

%jdbc(hive_interactive)
Drop materialized view if exists movies.mv_movie_ratings;
CREATE materialized view IF NOT EXISTS movies.mv_movie_ratings  AS Select movie_title.titleType, movie_title.primaryTitle, movie_title.originalTitle, ratings.averageRting from movie_title JOIN ratings ON (movie_title.tconst=ratings.tconst) where movie_title.startYear> 2001 and ratings.averageRting >5.5;
